package Exercise_4_22;

public class TabularTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("N"+"\t"+"10*N"+"\t"+"100*N"+"\t"+"1000*N");
		int i=1;
		while(i<=5){
			System.out.println(i+"\t"+10*i+"\t"+100*i+"\t"+1000*i);
			i++;
		}
		
		
	}

}
